import React, { useState, useEffect } from 'react';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import { jwtDecode } from 'jwt-decode';
import axios from 'axios';
import { Routes, Route, Link } from 'react-router-dom';
import { FiLogOut } from 'react-icons/fi';
import { Container, Row, Col, Button, Spinner, Alert } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import DettagliFilm from './DettagliFilm';

const clientId = '694573141536-tp3qpdtg19e16d4rftikofrr40ovi4sc.apps.googleusercontent.com';
const tmdbApiKey = 'f245ddf2fa039b4ac103b86de1fc2df4';

function App() {
  const [user, setUser] = useState(null);
  const [query, setQuery] = useState('');
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [watchlist, setWatchlist] = useState([]);
  const [message, setMessage] = useState('');

  const loadWatchlist = (email) => {
    const saved = JSON.parse(localStorage.getItem(`watchlist_${email}`)) || [];
    setWatchlist(saved);
  };

  const saveWatchlist = (email, list) => {
    localStorage.setItem(`watchlist_${email}`, JSON.stringify(list));
  };

  const handleLoginSuccess = (credentialResponse) => {
    const decoded = jwtDecode(credentialResponse.credential);
    setUser(decoded);
    loadWatchlist(decoded.email);
  };

  const handleLoginFailure = (error) => {
    console.log('Login Failed:', error);
  };

  const handleLogout = () => {
    setUser(null);
    setWatchlist([]);
  };

  const handleSearch = async (query) => {
    if (!query) {
      setMovies([]);
      return;
    }
    
    setIsLoading(true);
    
    try {
      const response = await axios.get(`https://api.themoviedb.org/3/search/movie`, {
        params: {
          api_key: tmdbApiKey,
          query: query,
        },
      });
      setMovies(response.data.results);
    } catch (error) {
      console.error('Errore durante la ricerca dei film:', error);
    }
    
    setIsLoading(false);
  };

  const showMessage = (text) => {
    setMessage(text);
    setTimeout(() => {
      setMessage('');
    }, 3000);
  };

  const handleAddToWatchlist = (movie) => {
    if (user && !watchlist.some((item) => item.id === movie.id)) {
      const updated = [...watchlist, movie];
      setWatchlist(updated);
      saveWatchlist(user.email, updated);
      showMessage('Film aggiunto alla Watchlist!');
    }
  };

  const handleRemoveFromWatchlist = (movieId) => {
    if (user) {
      const updated = watchlist.filter((item) => item.id !== movieId);
      setWatchlist(updated);
      saveWatchlist(user.email, updated);
      showMessage('Film rimosso dalla Watchlist!');
    }
  };

  useEffect(() => {
    const timeoutId = setTimeout(() => {
      handleSearch(query);
    }, 500);
    return () => clearTimeout(timeoutId);
  }, [query]);

  return (
    <GoogleOAuthProvider clientId={clientId}>
      <div className="App" style={{ backgroundColor: '#141414', color: '#fff' }}>
        {message && (
          <Alert variant="success" style={{ position: 'fixed', top: '10px', left: '50%', transform: 'translateX(-50%)' }}>
            {message}
          </Alert>
        )}

        {user && (
          <Button
            className="logout-button"
            onClick={handleLogout}
            style={{ position: 'absolute', top: '20px', right: '20px', backgroundColor: 'transparent', border: '2px solid #fff' }}
          >
            <FiLogOut size={20} />
            Logout
          </Button>
        )}

        <Container className="mt-5">
          <h2 className="text-center">Benvenuto in Movie World, {user ? user.name : 'Guest'}!</h2>
          
          {!user ? (
            <GoogleLogin
              onSuccess={handleLoginSuccess}
              onError={handleLoginFailure}
              useOneTap
            />
          ) : (
            <>
              <Row className="mb-4">
                <Col md={8} className="mx-auto">
                  <input
                    type="text"
                    placeholder="Cerca un film..."
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                    className="form-control"
                    style={{ marginBottom: '20px' }}
                  />
                </Col>
              </Row>

              {isLoading && (
                <div className="text-center">
                  <Spinner animation="border" variant="danger" />
                  <p>Caricamento...</p>
                </div>
              )}

              <Row>
                {movies.length > 0 && (
                  <div>
                    <h3>Risultati della ricerca:</h3>
                    <div className="d-flex flex-wrap">
                      {movies.map((movie) => (
                        <div key={movie.id} className="col-md-4 mb-4">
                          <div className="card">
                            {movie.poster_path && (
                              <img
                                src={`https://image.tmdb.org/t/p/w200${movie.poster_path}`}
                                className="card-img-top"
                                alt={movie.title}
                              />
                            )}
                            <div className="card-body">
                              <h5 className="card-title">{movie.title}</h5>
                              <Button
                                variant={watchlist.some((item) => item.id === movie.id) ? 'danger' : 'success'}
                                onClick={() => (watchlist.some((item) => item.id === movie.id) ? handleRemoveFromWatchlist(movie.id) : handleAddToWatchlist(movie))}
                              >
                                {watchlist.some((item) => item.id === movie.id) ? 'Rimuovi dalla Watchlist' : 'Aggiungi alla Watchlist'}
                              </Button>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                )}
              </Row>

              {watchlist.length > 0 && (
                <div className="mt-5">
                  <h3 className="text-center">La tua Watchlist:</h3>
                  <Row className="d-flex flex-wrap">
                    {watchlist.map((movie) => (
                      <div key={movie.id} className="col-md-4 mb-4">
                        <div className="card">
                          {movie.poster_path && (
                            <img
                              src={`https://image.tmdb.org/t/p/w200${movie.poster_path}`}
                              className="card-img-top"
                              alt={movie.title}
                            />
                          )}
                          <div className="card-body">
                            <h5 className="card-title">{movie.title}</h5>
                            <Button variant="danger" onClick={() => handleRemoveFromWatchlist(movie.id)}>
                              Rimuovi dalla Watchlist
                            </Button>
                          </div>
                        </div>
                      </div>
                    ))}
                  </Row>
                </div>
              )}
            </>
          )}
        </Container>

        <Routes>
          <Route path="/movie/:id" element={<DettagliFilm apiKey={tmdbApiKey} />} />
        </Routes>
      </div>
    </GoogleOAuthProvider>
  );
}

export default App;
