// DettagliFilm.js
import React from 'react';
import { Card } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const DettagliFilm = ({ movie }) => {
  const navigate = useNavigate();

  if (!movie) {
    return <p>Film non trovato.</p>;
  }

  return (
    <div className="d-flex justify-content-center align-items-center" style={{ minHeight: '80vh' }}>
      <Card style={{ width: '22rem', backgroundColor: '#1e1e1e', color: '#fff', borderRadius: '20px', padding: '20px' }}>
        <Card.Img 
          variant="top" 
          src={movie.Poster !== 'N/A' ? movie.Poster : 'https://via.placeholder.com/400x600?text=Nessuna+Immagine'} 
          alt={movie.Title} 
          style={{ borderRadius: '15px', marginBottom: '20px' }}
        />
        <Card.Body>
          <Card.Title className="mb-3" style={{ color: '#ffcc00' }}>{movie.Title}</Card.Title>
          <Card.Text><strong>Anno:</strong> {movie.Year}</Card.Text>
          <Card.Text><strong>Tipo:</strong> {movie.Type}</Card.Text>
          <Card.Text><strong>ID IMDb:</strong> {movie.imdbID}</Card.Text>
          <button 
            className="btn btn-warning mt-3" 
            onClick={() => navigate('/')}
          >
            Torna indietro
          </button>
        </Card.Body>
      </Card>
    </div>
  );
};

export default DettagliFilm;
