import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import axios from "axios";

function App() {
  const [movies, setMovies] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [watchlist, setWatchlist] = useState([]);

  useEffect(() => {
    fetchMovies();
  }, []);

  const fetchMovies = async () => {
    try {
      const response = await axios.get("https://api.themoviedb.org/3/movie/popular", {
        params: {
          api_key: "la_tua_api_key",
          language: "it-IT",
          page: 1,
        },
      });
      setMovies(response.data.results);
    } catch (error) {
      console.error("Errore durante il caricamento dei film:", error);
    }
  };

  const addToWatchlist = (movie) => {
    if (!watchlist.some((item) => item.id === movie.id)) {
      setWatchlist([...watchlist, movie]);
    }
  };

  const removeFromWatchlist = (id) => {
    setWatchlist(watchlist.filter((movie) => movie.id !== id));
  };

  const filteredMovies = movies.filter((movie) =>
    movie.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <Router>
      <div className="container mt-4">
        <h1 className="text-center mb-4">Movie World</h1>

        <div className="mb-4">
          <input
            type="text"
            placeholder="Cerca un film..."
            className="form-control"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        <nav className="mb-4">
          <Link to="/" className="btn btn-primary me-2">Home</Link>
          <Link to="/watchlist" className="btn btn-secondary">Watchlist ({watchlist.length})</Link>
        </nav>

        <Routes>
          <Route path="/" element={<MovieList movies={filteredMovies} addToWatchlist={addToWatchlist} watchlist={watchlist} removeFromWatchlist={removeFromWatchlist} />} />
          <Route path="/watchlist" element={<Watchlist watchlist={watchlist} removeFromWatchlist={removeFromWatchlist} />} />
        </Routes>
      </div>
    </Router>
  );
}

function MovieList({ movies, addToWatchlist, watchlist, removeFromWatchlist }) {
  return (
    <div className="row">
      {movies.map((movie) => (
        <div className="col-md-3 mb-4" key={movie.id}>
          <div className="card h-100">
            {movie.poster_path && (
              <img
                src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
                alt={movie.title}
                className="card-img-top"
              />
            )}
            <div className="card-body d-flex flex-column">
              <h5 className="card-title">{movie.title}</h5>
              <div className="mt-auto">
                {watchlist.some((item) => item.id === movie.id) ? (
                  <button
                    className="btn btn-danger btn-sm w-100"
                    onClick={() => removeFromWatchlist(movie.id)}
                  >
                    Rimuovi dalla Watchlist
                  </button>
                ) : (
                  <button
                    className="btn btn-primary btn-sm w-100"
                    onClick={() => addToWatchlist(movie)}
                  >
                    Aggiungi alla Watchlist
                  </button>
                )}
              </div>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

function Watchlist({ watchlist, removeFromWatchlist }) {
  return (
    <div className="row">
      {watchlist.length === 0 ? (
        <p>La tua watchlist è vuota.</p>
      ) : (
        watchlist.map((movie) => (
          <div className="col-md-3 mb-4" key={movie.id}>
            <div className="card h-100">
              {movie.poster_path && (
                <img
                  src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
                  alt={movie.title}
                  className="card-img-top"
                />
              )}
              <div className="card-body d-flex flex-column">
                <h5 className="card-title">{movie.title}</h5>
                <div className="mt-auto">
                  <button
                    className="btn btn-danger btn-sm w-100"
                    onClick={() => removeFromWatchlist(movie.id)}
                  >
                    Rimuovi dalla Watchlist
                  </button>
                </div>
              </div>
            </div>
          </div>
        ))
      )}
    </div>
  );
}

export default App;
