import React, { useState, useEffect } from 'react';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import { jwtDecode } from 'jwt-decode'; // Corretta importazione
import axios from 'axios';

const clientId = '694573141536-tp3qpdtg19e16d4rftikofrr40ovi4sc.apps.googleusercontent.com';
const tmdbApiKey = 'f245ddf2fa039b4ac103b86de1fc2df4';

function App() {
  const [user, setUser] = useState(null);
  const [query, setQuery] = useState('');
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [watchlist, setWatchlist] = useState([]); // Stato per la watchlist

  // Carica la watchlist dal localStorage quando l'app viene caricata
  useEffect(() => {
    const savedWatchlist = JSON.parse(localStorage.getItem('watchlist')) || [];
    setWatchlist(savedWatchlist);
  }, []);

  const handleLoginSuccess = (credentialResponse) => {
    const decoded = jwtDecode(credentialResponse.credential);  // Uso jwtDecode
    setUser(decoded);
  };

  const handleLoginFailure = (error) => {
    console.log('Login Failed:', error);
  };

  const handleSearch = async (query) => {
    if (!query) {
      setMovies([]);
      return;
    }
    
    setIsLoading(true);
    
    try {
      const response = await axios.get(`https://api.themoviedb.org/3/search/movie`, {
        params: {
          api_key: tmdbApiKey,
          query: query,
        },
      });
      setMovies(response.data.results);
    } catch (error) {
      console.error('Errore durante la ricerca dei film:', error);
    }
    
    setIsLoading(false);
  };

  const handleAddToWatchlist = (movie) => {
    // Aggiunge il film alla watchlist se non è già presente
    if (!watchlist.some((item) => item.id === movie.id)) {
      const updatedWatchlist = [...watchlist, movie];
      setWatchlist(updatedWatchlist);
      localStorage.setItem('watchlist', JSON.stringify(updatedWatchlist));  // Salva la watchlist nel localStorage
    }
  };

  const handleRemoveFromWatchlist = (movieId) => {
    // Rimuove il film dalla watchlist
    const updatedWatchlist = watchlist.filter((item) => item.id !== movieId);
    setWatchlist(updatedWatchlist);
    localStorage.setItem('watchlist', JSON.stringify(updatedWatchlist));  // Salva la watchlist aggiornata nel localStorage
  };

  useEffect(() => {
    const timeoutId = setTimeout(() => {
      handleSearch(query);
    }, 500);  // Debounce di 500ms
    
    return () => clearTimeout(timeoutId);  // Pulisce il timeout se l'utente smette di digitare
  }, [query]);

  return (
    <GoogleOAuthProvider clientId={clientId}>
      <div style={{ padding: '20px' }}>
        {!user ? (
          <GoogleLogin
            onSuccess={handleLoginSuccess}
            onError={handleLoginFailure}
          />
        ) : (
          <div>
            <h2>Benvenuto in Movie World, {user.name}!</h2>

            <div style={{ marginTop: '20px' }}>
              <input
                type="text"
                placeholder="Cerca un film..."
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                style={{ padding: '10px', width: '300px' }}
              />
            </div>

            {isLoading && <div>Caricamento...</div>}

            <div style={{ marginTop: '30px' }}>
              {movies.length > 0 && (
                <div>
                  <h3>Risultati:</h3>
                  <div style={{ display: 'flex', flexWrap: 'wrap' }}>
                    {movies.map((movie) => (
                      <div key={movie.id} style={{ margin: '10px', width: '200px', textAlign: 'center' }}>
                        {movie.poster_path && (
                          <img
                            src={`https://image.tmdb.org/t/p/w200${movie.poster_path}`}
                            alt={movie.title}
                            style={{ width: '100%', borderRadius: '8px' }}
                          />
                        )}
                        <div>{movie.title}</div>
                        {/* Aggiungi o rimuovi dalla watchlist */}
                        {watchlist.some((item) => item.id === movie.id) ? (
                          <button onClick={() => handleRemoveFromWatchlist(movie.id)}>Rimuovi dalla Watchlist</button>
                        ) : (
                          <button onClick={() => handleAddToWatchlist(movie)}>Aggiungi alla Watchlist</button>
                        )}
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {watchlist.length > 0 && (
                <div style={{ marginTop: '50px' }}>
                  <h3>La tua Watchlist:</h3>
                  <div style={{ display: 'flex', flexWrap: 'wrap' }}>
                    {watchlist.map((movie) => (
                      <div key={movie.id} style={{ margin: '10px', width: '200px', textAlign: 'center' }}>
                        {movie.poster_path && (
                          <img
                            src={`https://image.tmdb.org/t/p/w200${movie.poster_path}`}
                            alt={movie.title}
                            style={{ width: '100%', borderRadius: '8px' }}
                          />
                        )}
                        <div>{movie.title}</div>
                        <button onClick={() => handleRemoveFromWatchlist(movie.id)}>Rimuovi dalla Watchlist</button>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    </GoogleOAuthProvider>
  );
}

export default App;
