import React, { useState, useEffect } from 'react';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import { jwtDecode } from 'jwt-decode'; 
import axios from 'axios';

const clientId = '694573141536-tp3qpdtg19e16d4rftikofrr40ovi4sc.apps.googleusercontent.com';
const tmdbApiKey = 'f245ddf2fa039b4ac103b86de1fc2df4';

function App() {
  const [user, setUser] = useState(null);
  const [query, setQuery] = useState('');
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [watchlist, setWatchlist] = useState([]);
  const [message, setMessage] = useState(''); // <- stato per i messaggi

  const loadWatchlist = (email) => {
    const saved = JSON.parse(localStorage.getItem(`watchlist_${email}`)) || [];
    setWatchlist(saved);
  };

  const saveWatchlist = (email, list) => {
    localStorage.setItem(`watchlist_${email}`, JSON.stringify(list));
  };

  const handleLoginSuccess = (credentialResponse) => {
    const decoded = jwtDecode(credentialResponse.credential);
    setUser(decoded);
    loadWatchlist(decoded.email);
  };

  const handleLoginFailure = (error) => {
    console.log('Login Failed:', error);
  };

  const handleLogout = () => {
    setUser(null);
    setWatchlist([]);
  };

  const handleSearch = async (query) => {
    if (!query) {
      setMovies([]);
      return;
    }
    
    setIsLoading(true);
    
    try {
      const response = await axios.get(`https://api.themoviedb.org/3/search/movie`, {
        params: {
          api_key: tmdbApiKey,
          query: query,
        },
      });
      setMovies(response.data.results);
    } catch (error) {
      console.error('Errore durante la ricerca dei film:', error);
    }
    
    setIsLoading(false);
  };

  const showMessage = (text) => {
    setMessage(text);
    setTimeout(() => {
      setMessage('');
    }, 3000); // sparisce dopo 3 secondi
  };

  const handleAddToWatchlist = (movie) => {
    if (user && !watchlist.some((item) => item.id === movie.id)) {
      const updated = [...watchlist, movie];
      setWatchlist(updated);
      saveWatchlist(user.email, updated);
      showMessage('Film aggiunto alla Watchlist!');
    }
  };

  const handleRemoveFromWatchlist = (movieId) => {
    if (user) {
      const updated = watchlist.filter((item) => item.id !== movieId);
      setWatchlist(updated);
      saveWatchlist(user.email, updated);
      showMessage('Film rimosso dalla Watchlist!');
    }
  };

  useEffect(() => {
    const timeoutId = setTimeout(() => {
      handleSearch(query);
    }, 500);
    return () => clearTimeout(timeoutId);
  }, [query]);

  return (
    <GoogleOAuthProvider clientId={clientId}>
      <div style={{ padding: '20px' }}>
        {/* Notifica */}
        {message && (
          <div style={{
            position: 'fixed',
            top: '20px',
            left: '50%',
            transform: 'translateX(-50%)',
            backgroundColor: '#4caf50',
            color: 'white',
            padding: '10px 20px',
            borderRadius: '8px',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
            zIndex: 1000
          }}>
            {message}
          </div>
        )}

        {!user ? (
          <GoogleLogin
            onSuccess={handleLoginSuccess}
            onError={handleLoginFailure}
          />
        ) : (
          <div>
            <h2>Benvenuto in Movie World, {user.name}!</h2>

            <button 
              onClick={handleLogout} 
              style={{
                padding: '10px 20px',
                marginTop: '10px',
                backgroundColor: '#ff4d4f',
                color: 'white',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer'
              }}
            >
              Logout
            </button>

            <div style={{ marginTop: '20px' }}>
              <input
                type="text"
                placeholder="Cerca un film..."
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                style={{ padding: '10px', width: '300px' }}
              />
            </div>

            {isLoading && <div>Caricamento...</div>}

            <div style={{ marginTop: '30px' }}>
              {movies.length > 0 && (
                <div>
                  <h3>Risultati:</h3>
                  <div style={{ display: 'flex', flexWrap: 'wrap' }}>
                    {movies.map((movie) => (
                      <div key={movie.id} style={{ margin: '10px', width: '200px', textAlign: 'center' }}>
                        {movie.poster_path && (
                          <img
                            src={`https://image.tmdb.org/t/p/w200${movie.poster_path}`}
                            alt={movie.title}
                            style={{ width: '100%', borderRadius: '8px' }}
                          />
                        )}
                        <div>{movie.title}</div>
                        {watchlist.some((item) => item.id === movie.id) ? (
                          <button onClick={() => handleRemoveFromWatchlist(movie.id)}>Rimuovi dalla Watchlist</button>
                        ) : (
                          <button onClick={() => handleAddToWatchlist(movie)}>Aggiungi alla Watchlist</button>
                        )}
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {watchlist.length > 0 && (
                <div style={{ marginTop: '50px' }}>
                  <h3>La tua Watchlist:</h3>
                  <div style={{ display: 'flex', flexWrap: 'wrap' }}>
                    {watchlist.map((movie) => (
                      <div key={movie.id} style={{ margin: '10px', width: '200px', textAlign: 'center' }}>
                        {movie.poster_path && (
                          <img
                            src={`https://image.tmdb.org/t/p/w200${movie.poster_path}`}
                            alt={movie.title}
                            style={{ width: '100%', borderRadius: '8px' }}
                          />
                        )}
                        <div>{movie.title}</div>
                        <button onClick={() => handleRemoveFromWatchlist(movie.id)}>Rimuovi dalla Watchlist</button>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    </GoogleOAuthProvider>
  );
}

export default App;
