// DettagliFilm.jsx

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const DettagliFilm = ({ apiKey }) => {
  const { id } = useParams();
  const [movieDetails, setMovieDetails] = useState(null);
  const [cast, setCast] = useState([]);
  const [trailer, setTrailer] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const movieResponse = await axios.get(`https://api.themoviedb.org/3/movie/${id}`, {
          params: { api_key: apiKey },
        });
        setMovieDetails(movieResponse.data);

        const castResponse = await axios.get(`https://api.themoviedb.org/3/movie/${id}/credits`, {
          params: { api_key: apiKey },
        });
        setCast(castResponse.data.cast);

        const trailerResponse = await axios.get(`https://api.themoviedb.org/3/movie/${id}/videos`, {
          params: { api_key: apiKey },
        });
        setTrailer(trailerResponse.data.results[0]?.key); // Prende il primo trailer
      } catch (error) {
        console.error('Errore nel recuperare i dettagli del film:', error);
      }
    };

    fetchData();
  }, [id, apiKey]);

  return (
    movieDetails && (
      <div className="movie-details">
        <h1>{movieDetails.title}</h1>
        {movieDetails.poster_path && (
          <img
            src={`https://image.tmdb.org/t/p/w500${movieDetails.poster_path}`}
            alt={movieDetails.title}
          />
        )}
        <p>{movieDetails.overview}</p>

        {trailer && (
          <div className="trailer-container">
            <h3>Trailer</h3>
            <iframe
              width="560"
              height="315"
              src={`https://www.youtube.com/embed/${trailer}`}
              title="YouTube video player"
              frameBorder="0"
              allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            ></iframe>
          </div>
        )}

        <h3>Cast:</h3>
        <div className="cast-container">
          {cast.slice(0, 10).map((actor) => (
            <div key={actor.cast_id} className="cast-member">
              {actor.profile_path && (
                <img
                  src={`https://image.tmdb.org/t/p/w200${actor.profile_path}`}
                  alt={actor.name}
                />
              )}
              <div>{actor.name}</div>
              <div>{actor.character}</div>
            </div>
          ))}
        </div>
      </div>
    )
  );
};

export default DettagliFilm;
