import React, { useState } from 'react';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import { jwtDecode } from 'jwt-decode';  // ✅ Import corretto

const clientId = '694573141536-tp3qpdtg19e16d4rftikofrr40ovi4sc.apps.googleusercontent.com';

function App() {
  const [user, setUser] = useState(null);

  const handleLoginSuccess = (credentialResponse) => {
    console.log('Login Success:', credentialResponse);
    const decoded = jwtDecode(credentialResponse.credential);  // ✅ Decodifica corretta
    console.log('Decoded user:', decoded);
    setUser(decoded);
  };

  const handleLoginFailure = (error) => {
    console.log('Login Failed:', error);
  };

  const handleLogout = () => {
    setUser(null);
  };

  return (
    <GoogleOAuthProvider clientId={clientId}>
      <div style={{ textAlign: 'center', marginTop: '50px' }}>
        {!user ? (
          <GoogleLogin
            onSuccess={handleLoginSuccess}
            onError={handleLoginFailure}
          />
        ) : (
          <div>
            <h1>Benvenuto, {user.name}!</h1>
            <img 
              src={user.picture} 
              alt="Foto profilo" 
              style={{ borderRadius: '50%', marginTop: '20px' }} 
            />
            <br />
            <button 
              onClick={handleLogout} 
              style={{
                marginTop: '20px',
                padding: '10px 20px',
                borderRadius: '8px',
                backgroundColor: '#4285F4',
                color: 'white',
                border: 'none',
                cursor: 'pointer'
              }}
            >
              Esci
            </button>
          </div>
        )}
      </div>
    </GoogleOAuthProvider>
  );
}

export default App;
